mapper.insert:
insert into articles
    (user_id, content, create_time, flag_private, room_id)
values ( #{userId}, #{content}, now(), #{flagPrivate}, #{roomId});

mapper.select:
select a.id as article_id, a.user_id, a.content, a.create_time, a.flag_private, a.modify_time, a.room_id
from articles a
where a.id = #{id};

select count(*) as like_count
from article_likes
where article_id = #{articleId};

select *
from article_images
where article_id = #{articleId}
order by id;

select al.user_id
from article_likes al
where article_id = #{articleId}

mapper.insert:
insert into comments
    (article_id, user_id, parent_comment_id, content, create_time)
values (#{articleId}, #{userId}, #{parentCommentId}, #{content}, now());

mapper.select:
select u.id
from users u
where id = #{userId}
