mapper.update:
UPDATE daily_missions
SET normal_vote = #{targetId}
WHERE participant_id = #{participantId}
		  AND day = (
		  SELECT MAX(day)
		  FROM daily_missions
		  WHERE participant_id = #{participantId}
	  );

UPDATE daily_missions
SET mafia_vote = #{targetId}
WHERE participant_id = #{participantId}
		  AND day = (
		  SELECT MAX(day)
		  FROM daily_missions
		  WHERE participant_id = #{participantId}
	  );

UPDATE daily_missions
SET docotr_vote = #{targetId}
WHERE participant_id = #{participantId}
		  AND day = (
		  SELECT MAX(day)
		  FROM daily_missions
		  WHERE participant_id = #{participantId}
	  );

mapper.delete:
DELETE
FROM participants
WHERE room_id = 'room1' AND user_id = #{userId}

mapper.select:
SELECT EXISTS(
	SELECT 1
	FROM participants
	WHERE user_id = #{userId}
	  AND room_id = 'room1'
) AS RandomGameApplyStatus;

SELECT EXISTS(
	SELECT 1
	FROM participants
	WHERE user_id = #{userId}
	  AND room_id = #{roomId}
) AS IsParticipantInRoom;
